mapper.update:
UPDATE order_table
SET state = "approved"
WHERE order_id = #{order_id}

UPDATE order_table
SET state = "rejected"
WHERE order_id = #{order_id}

UPDATE order_table
SET state = IF(state = "approved", "unpaid_delivered", IF(state = "paid_undelivered", "paid_delivered", state))
WHERE order_id = #{order_id}
