mapper.select:
SELECT COUNT(*)
FROM client_table
WHERE client_name LIKE CONCAT("%", #{query}, "%") OR phone = #{query}

SELECT client_id, client_name, sex, phone, address, points, wholesale_client, retail_client
FROM client_table
WHERE client_name LIKE CONCAT("%", #{query}, "%") OR phone = #{query}
LIMIT #{page}, #{limit}

mapper.update:
UPDATE product_table
SET product_name = #{product_name}, type = #{type}, unit = #{unit},
purchase_price = #{purchase_price}, wholesale_price = #{wholesale_price}, retail_price = #{retail_price}
WHERE product_id = #{product_id}

UPDATE client_table
SET client_name = #{client_name}, sex = #{sex}, phone = #{phone},
address = #{address}, wholesale_client = #{wholesale_client}, retail_client = #{retail_client}
WHERE client_id = #{client_id}
