mapper.select:
SELECT client_id, client_name, sex, phone, address, wallet 
FROM client_table
LIMIT #{offset}, #{limit}

SELECT client_id, client_name, sex, phone, address, wallet
FROM client_table
WHERE client_id = #{clientId}

SELECT client_id, client_name, sex, phone, address, wallet FROM client_table
WHERE client_name LIKE CONCAT("%", #{clientName}, "%")
LIMIT #{offset}, #{limit}

SELECT product_id, product_name, type, unit, purchase_price, retail_price
FROM product_table
LIMIT #{offset}, #{limit}

SELECT product_id, product_name, type, unit, purchase_price, retail_price
FROM product_table
WHERE product_id = #{product_id}

mapper.update:
UPDATE depot_prod_table
SET count = depot_prod_table.count-#{count}
WHERE depot_name = "门店" AND product_id = #{product_id}
